function bp_ps_clear_radio(e){for(var t=new Event("change"),o=(e=document.getElementById(e)).getElementsByTagName("input"),n=0;n<o.length;n++)o[n].checked="",o[n].dispatchEvent(t)}function bp_ps_autocomplete(e,t,o){e=document.getElementById(e);var n={types:["geocode"]},a=new google.maps.places.Autocomplete(e,n);google.maps.event.addListener(a,"place_changed",function(){var e=a.getPlace();document.getElementById(t).value=e.geometry.location.lat(),document.getElementById(o).value=e.geometry.location.lng()})}function bp_ps_locate(e,t,o){if(navigator.geolocation){var n={timeout:5e3};navigator.geolocation.getCurrentPosition(function(n){document.getElementById(t).value=n.coords.latitude,document.getElementById(o).value=n.coords.longitude,bp_ps_address(n,e)},function(e){alert("ERROR "+e.code+": "+e.message)},n)}else alert("ERROR: Geolocation is not supported by this browser")}function bp_ps_address(e,t){var o=new google.maps.Geocoder,n={lat:e.coords.latitude,lng:e.coords.longitude};o.geocode({location:n},function(e,o){"OK"===o?e[0]?document.getElementById(t).value=e[0].formatted_address:alert("ERROR: Geocoder found no results"):alert("ERROR: Geocoder status: "+o)})}